import React, { useState } from 'react';
import './App.css';
import AddTasks from './components/AddTasks';
import TestCard from './components/TaskCard';
import TaskForm from './components/TaskForm';
import { ClickContext, TaskFields, TaskFormContext } from './Context'

function Title(): JSX.Element {
    return <h2>Hello World!</h2>
}

function App(): JSX.Element {
  const [showForm, setShowForm] = useState(false)
  const [taskName, setTaskName] = useState("")
  const [taskDesc, setTaskDesc] = useState("")
  const [taskDue, setTaskDue] = useState("")

  function handleClick() {
    console.log("clicked")
    setShowForm(!showForm)
  }
  return(
    <ClickContext.Provider value = { handleClick }>
    <TaskFormContext.Provider value= {{
      taskName, setTaskName,
      taskDesc, setTaskDesc,
      taskDue, setTaskDue
    }}>
      <>
        <Title />
        <TestCard />
        <AddTasks />
        {showForm ? <TaskForm /> : null}
      </>
    </ TaskFormContext.Provider>
    </ ClickContext.Provider>
  )
}

export default App;
